ASE_URL}/api${endpoint}`; const config = { headers: { 'Content-Type': 'application/json', ...(token && { 'Authorization': `Bearer ${token}` }) }, ...options }; try { const response = await fetch(url, config); const data = await response.json(); if (!response.ok) { throw new Error(data.message || `HTTP error! status: ${response.status}`); } return data; } catch (error) { console.error('API请求失败:', error); throw error; } } // 状态管理API调用 const StateAPI = { // 获取用户状态 async getStatus() { return await apiRequest('/state/status'); }, // 激活账号 async activate() { return await apiRequest('/state/activate', { method: 'POST' }); }, // 复购 async repurchase() { return await apiRequest('/state/repurchase', { method: 'POST' }); }, // 同步状态 async syncState(localState) { return await apiRequest('/state/sync', { method: 'PUT', body: JSON.stringify(localState) }); }, // 切换状态（开发用） async switchState(newState) { return await apiRequest('/state/switch', { method: 'POST', body: JSON.stringify({ state: newState }) }); } }; /* 全局状态变量 */ let currentState = 1; let countdownStart = null; // 开启倒计时的时间戳 let inviteStart = null; // 邀请链接生效时间戳 let countdownTimer = null; // 模拟用户钱包余额，用于红包和提现功能 let walletBalance = 0; // 交易记录列表 let transactions = []; // 提现手续费比例，默认为 5%（0.05），可通过答题任务降低，最低 1%（0.01） let withdrawFeeRate = 0.05; // 新增状态管理变量 let userLevel = 0; // 用户等级 0:未激活 1:已激活 let completedNewbieTasks = 0; // 已完成新手任务数量 let quizCompleted = false; // 答题任务是否完成 let godTasksUnlocked = false; // 大神任务是否解锁 let teamMembers = 0; // 团队成员数量 let totalEarnings = 0; // 总收益 /** * 从 localStorage 初始化状态，包括余额、任务完成状态和交易记录。 */ function loadState() { const savedState = localStorage.getItem('appState'); if (savedState) { try { const state = JSON.parse(savedState); currentState = state.currentState || 1; countdownStart = state.countdownStart; inviteStart = state.inviteStart; walletBalance = state.walletBalance || 0; transactions = state.transactions || []; userLevel = state.userLevel || 0; completedNewbieTasks = state.completedNewbieTasks || 0; quizCompleted = state.quizCompleted || false; godTasksUnlocked = state.godTasksUnlocked || false; teamMembers = state.teamMembers || 0; totalEarnings = state.totalEarnings || 0; withdrawFeeRate = state.withdrawFeeRate || 0.05; // 同步团队收益：总收益 = 团队人数 * 10 const teamData = JSON.parse(localStorage.getItem('teamData') || '{}'); if (teamData.totalMembers) { totalEarnings = teamData.totalMembers * 10; teamMembers = teamData.totalMembers; } // 恢复任务状态 if (state.newTasks) { state.newTasks.forEach((task, index) => { if (newTasks[index]) newTasks[index].done = task.done; }); // 同步completedNewbieTasks变量，确保与newTasks数组状态一致 completedNewbieTasks = newTasks.filter(t => t.done).length; } if (state.quizTasks) { state.quizTasks.forEach((task, index) => { if (quizTasks[index]) quizTasks[index].done = task.done; }); } if (state.godTasks) { state.godTasks.forEach((task, index) => { if (godTasks[index]) godTasks[index].done = task.done; }); } } catch (e) { console.warn('无法解析 appState', e); } } } /** * 将当前状态保存到 localStorage。 */ function saveState() { // 同步团队收益：总收益 = 团队人数 * 10 const teamData = JSON.parse(localStorage.getItem('teamData') || '{}'); if (teamData.totalMembers) { totalEarnings = teamData.totalMembers * 10; } const state = { currentState, countdownStart, inviteStart, walletBalance, transactions, userLevel, completedNewbieTasks, quizCompleted, godTasksUnlocked, teamMembers, totalEarnings, withdrawFeeRate, newTasks: newTasks.map(t => ({ ...t })), quizTasks: quizTasks.map(t => ({ ...t })), godTasks: godTasks.map(t => ({ ...t })) }; localStorage.setItem('appState', JSON.stringify(state)); } // 与后端同步状态 async function syncWithBackend() { try { console.log('正在与后端同步状态...'); const response = await StateAPI.getStatus(); if (response.success && response.data) { const backendState = response.data; // 更新前端状态 currentState = backendState.status; // 如果后端有倒计时信息，同步倒计时 if (backendState.countdown_end_time) { const endTime = new Date(backendState.countdown_end_time); const now = new Date(); if (endTime > now) { countdownStart = now.getTime(); // 计算倒计时开始时间（168小时前） const startTime = new Date(endTime.getTime() - 168 * 60 * 60 * 1000); countdownStart = startTime.getTime(); } } // 同步其他数据 if (backendState.balance !== undefined) { walletBalance = backendState.balance; } if (backendState.total_earnings !== undefined) { totalEarnings = backendState.total_earnings; } if (backendState.team_count !== undefined) { teamMembers = backendState.team_count; } console.log('后端状态同步成功:', backendState); // 保存同步后的状态 saveState(); // 重新渲染页面 renderState(); } } catch (error) { console.warn('后端状态同步失败，使用本地状态:', error.message); // 继续使用本地状态，不影响用户体验 } } // 生成用户专属邀请码 function generateUserInviteCode() { const userEmail = localStorage.getItem('userEmail'); if (userEmail) { // 使用邮箱前缀 + 随机6位字符生成邀请码 const emailPrefix = userEmail.split('@')[0].substring(0, 6).toUpperCase(); const randomSuffix = Math.random().toString(36).substring(2, 8).toUpperCase(); return emailPrefix + randomSuffix; } // 如果没有邮箱，生成随机邀请码 return 'USER' + Math.random().toString(36).substring(2, 8).toUpperCase(); } // WebSocket连接和实时通知功能 let websocket = null; function initWebSocket() { // 获取JWT token const token = localStorage.getItem('token'); if (!token) { console.log('未找到token，跳过WebSocket连接'); return; } // 连接WebSocket服务器（使用正确的路径和token） websocket = new WebSocket(`ws://localhost:3000/ws?token=${encodeURIComponent(token)}`); websocket.onopen = function(event) { console.log('WebSocket连接已建立'); }; websocket.onmessage = function(event) { try { const data = JSON.parse(event.data); handleWebSocketMessage(data); } catch (error) { console.error('解析WebSocket消息失败:', error); } }; websocket.onclose = function(event) { console.log('WebSocket连接已关闭'); // 5秒后尝试重连 setTimeout(initWebSocket, 5000); }; websocket.onerror = function(error) { console.error('WebSocket错误:', error); }; } function handleWebSocketMessage(data) { switch (data.type) { case 'redpacket_notification': // 红包通知 showDialog( '红包提醒', `新的红包活动开始了！快去抢红包吧！`, [{ text: '立即抢红包', action: 'grabRedPacket' }, { text: '稍后再说', action: 'close' }], 'redpacket' ); break; case 'balance_update': // 余额更新通知 updateWalletBalance(data.newBalance, data.reason); break; case 'task_reminder': // 任务提醒 showDialog( '任务提醒', data.message, [{ text: '查看任务', action: 'showTasks' }, { text: '稍后再说', action: 'close' }], 'success' ); break; case 'system_notification': // 系统通知 showDialog( '系统通知', data.message, [{ text: '确定', action: 'close' }], 'success' ); break; default: console.log('未知的WebSocket消息类型:', data.type); } } // 更新钱包余额函数 function updateWalletBalance(newBalance, reason = '') { const oldBalance = walletBalance; walletBalance = newBalance; // 保存到localStorage localStorage.setItem('walletBalance', walletBalance.toString()); // 重新渲染页面以更新显示 renderState(); // 显示余额变化通知 if (reason) { const changeAmount = newBalance - oldBalance; const changeText = changeAmount > 0 ? `+${changeAmount}` : changeAmount.toString(); showDialog( '余额变化', `${reason}
余额变化: ${changeText}元
